INSERT INTO attractions
(id, name, description, price, web_link, picture_url, city_id)
VALUES
(1, 'Koishikawa Korakuen', 'Koishikawa Korakuen (小石川後楽園, Koishikawa Kōrakuen) is one of Tokyo''s oldest and best Japanese gardens. It was built in the early Edo Period (1600-1867) at the Tokyo residence of the Mito branch of the ruling Tokugawa family. Like its namesake in Okayama, the garden was named Korakuen after a poem encouraging a ruler to enjoy pleasure only after achieving happiness for his people. Koishikawa is the district in which the garden is located in.', 300, 'https://www.tokyo-park.or.jp/park/format/index030.html', 'ggg', 2),
(2, 'Tokyo Imperial Palace', 'The current Imperial Palace (皇居, Kōkyo) is located on the former site of Edo Castle, a large park area surrounded by moats and massive stone walls in the center of Tokyo, a short walk from Tokyo Station. It is the residence of Japan''s Imperial Family.', 0, 'https://sankan.kunaicho.go.jp/english/index.html', 'ggg', 2),
(3, 'Sensōji Temple', 'Sensoji (浅草寺, Sensōji, also known as Asakusa Kannon Temple) is a Buddhist temple located in Asakusa. It is one of Tokyo''s most colorful and popular temples.', 0, 'http://www.senso-ji.jp/', 'ggg', 2),
(4, 'Shibamata Taishakuten', 'Most of Taishakuten''s buildings are covered in wooden carvings, but the piece de resistance can be viewed in the paid area at the back of the Taishakudo Hall. Panels of intricate carvings cover the outer walls of the hall, each depicting a scene from Buddhist scripture and local folklore. The panels are only brushed once a year to remove cobwebs and otherwise remain untouched to preserve their longevity.', 400, '', 'ggg', 2),
(5, 'Rikugien', 'Rikugien (六義園) is often considered Tokyo''s most beautiful Japanese landscape garden alongside Koishikawa Korakuen. Built around 1700 for the 5th Tokugawa Shogun, Rikugien literally means "six poems garden" and reproduces in miniature 88 scenes from famous poems. The garden is a good example of an Edo Period strolling garden and features a large central pond surrounded by manmade hills and forested areas, all connected by a network of trails.', 300, 'https://www.tokyo-park.or.jp/teien/en/rikugien/', 'ggg', 2),
(6, 'Ghibli Museum', 'The Ghibli Museum (三鷹の森ジブリ美術館, Mitaka no Mori Ghibli Bijutsukan) is the animation and art museum of Miyazaki Hayao''s Studio Ghibli, one of Japan''s most famous animation studios. They have produced many feature length films with worldwide distribution such as My Neighbor Totoro, Princess Mononoke, Spirited Away and Ponyo on the Cliff by the Sea.', 1000, 'http://www.ghibli-museum.jp/en/', 'ggg', 2),
(7, 'Todoroki Park', 'The Todoroki Valley Park (等々力渓谷公園, Todoroki Keikoku Kōen) is one of Tokyo''s most unconventional green spaces. Located in the middle of the densely populated Setagaya city ward, it is an attractive walking trail, leading for about one kilometer through a narrow, wooded valley along a small river and takes about 20 to 30 minutes to walk through.', 0, 'http://www.todoroki.net/', 'ggg', 2),
(8, 'Oedo Onsen Monogatari', 'Pumped up from 1400 meters deep, our natural hot spring feel soft and smooth on the skin. You will enjoy prolonged effect of warmth even after you get out of the bath, and will feel relaxed inside and out. Soak yourself in Big Common Bath, then try Ashi-Yu (Foot Bath) while chatting with your family and loved ones – at Ooedo-Onsen Monogatari, we offer many different baths to suit your need – so you can enjoy Onsen your way.', 2700, 'https://daiba.ooedoonsen.jp/en/', 'ggg', 2),
(9, 'Takao Mountain', 'Mount Takao (高尾山, Takaosan) is one of the closest natural recreation areas to central Tokyo, offering beautiful scenery, an interesting temple and attractive hiking opportunities. Although outside the city center, the mountain is still located within metropolitan Tokyo and takes only 50 minutes and 390 yen to reach from Shinjuku.', 500, 'ggg', 'ggg', 2),
(10, 'Gotokuji Temple', 'Gotokuji Temple, located in the Setagaya ward of Tokyo, is a Buddhist temple that is said to be the birthplace of the maneki-neko, or “luck-inviting cat figurine.” These small statues, which portray a cat sitting up and beckoning with its front paw, have become quite popular all over the world with cat-lovers. Although there are many different kinds of maneki-neko raising either their right or left paws, Gotokuji Temple specializes in the right-pawed version of this good-luck symbol.', 0, '', 'ggg', 2),
(11, 'Shinjuku Gyoen', 'Shinjuku Gyoen (新宿御苑) is one of Tokyo''s largest and most popular parks. Located just a short walk from Shinjuku Station, the paid park''s spacious lawns, meandering walking paths and tranquil scenery provide a relaxing escape from the busy urban center around it. In spring Shinjuku Gyoen becomes one of the best places in the city to see cherry blossoms.', 200, 'http://www.env.go.jp/garden/shinjukugyoen/english/', 'ggg', 2),
(12, 'Nijo Castle ', 'Nijo Castle (二条城, Nijōjō) was built in 1603 as the Kyoto residence of Tokugawa Ieyasu, the first shogun of the Edo Period (1603-1867). His grandson Iemitsu completed the castle''s palace buildings 23 years later and further expanded the castle by adding a five story castle keep.', 600, 'http://www2.city.kyoto.lg.jp/bunshi/nijojo/english/index.html', 'ggg', 1),
(13, 'Kyoto Imperial Palace', 'The Kyoto Imperial Palace (京都御所, Kyōto Gosho) used to be the residence of Japan''s Imperial Family until 1868, when the emperor and capital were moved from Kyoto to Tokyo. It is located in the spacious Kyoto Imperial Park (京都御苑, Kyōto Gyoen), an attractive park in the center of the city that also encompasses the Sento Imperial Palace and a few other attractions.', 0, 'http://sankan.kunaicho.go.jp/english/about/kyoto.html', 'ggg', 1),
(14, 'Amanohashidate', 'Amanohashidate (天橋立) is a pine covered sandbar that spans the mouth of Miyazu Bay in the scenic, coastal region of northern Kyoto Prefecture. Viewed from the mountains at either end of the bay, the Amanohashidate Sandbar (which roughly translates to "bridge in heaven") looks like a pathway between heaven and earth. The scene has been admired for centuries and is ranked among Japan''s three most scenic views.', 700, 'http://www.amanohashidate.jp/lang/en/', 'ggg', 1),
(15, 'Sanzenin Temple', 'Sanzenin Temple (三千院) is the main attraction of the rural town of Ohara, which is located about an hour north of central Kyoto. The approach from Ohara bus stop to Sanzenin is lined with shops and restaurants catering to temple visitors, and there are a number of smaller temples in the vicinity. Sanzenin Temple itself has large temple grounds and a variety of buildings, gardens and walking paths.', 700, 'http://www.sanzenin.or.jp/en/', 'ggg', 1),
(16, 'Ryoanji Temple', 'Ryoanji Temple (龍安寺, Ryōanji) is the site of Japan''s most famous rock garden, which attracts hundreds of visitors every day. Originally an aristocrat''s villa during the Heian Period, the site was converted into a Zen temple in 1450 and belongs to the Myoshinji school of the Rinzai sect of Zen Buddhism, whose head temple stands just a kilometer to the south.', 500, 'http://www.ryoanji.jp/smph/eng/', 'ggg', 1),
(17, 'Fushimi Inari Shrine', 'Fushimi Inari Shrine (伏見稲荷大社, Fushimi Inari Taisha) is an important Shinto shrine in southern Kyoto. It is famous for its thousands of vermilion torii gates, which straddle a network of trails behind its main buildings. The trails lead into the wooded forest of the sacred Mount Inari, which stands at 233 meters and belongs to the shrine grounds.', 0, 'http://inari.jp/en/', 'ggg', 1),
(18, 'Tenryu-ji Temple', 'Tenryuji (天龍寺, Tenryūji) is the most important temple in Kyoto''s Arashiyama district. It was ranked first among the city''s five great Zen temples, and is now registered as a world heritage site. Tenryuji is the head temple of its own school within the Rinzai Zen sect of Japanese Buddhism.', 500, 'http://www.tenryuji.com/en/', 'ggg', 1),
(19, 'Arashiyama bamboo forest', 'Arashiyama (嵐山) is a pleasant, touristy district in the western outskirts of Kyoto. The area has been a popular destination since the Heian Period (794-1185), when nobles would enjoy its natural setting. Arashiyama is particularly popular during the cherry blossom and fall color seasons.', 0, '', 'ggg', 1),
(20, 'Kiyomizu-dera Temple', 'Kiyomizudera (清水寺, literally "Pure Water Temple") is one of the most celebrated temples of Japan. It was founded in 780 on the site of the Otowa Waterfall in the wooded hills east of Kyoto, and derives its name from the fall''s pure waters. The temple was originally associated with the Hosso sect, one of the oldest schools within Japanese Buddhism, but formed its own Kita Hosso sect in 1965. In 1994, the temple was added to the list of UNESCO world heritage sites.', 400, 'http://kiyomizudera.or.jp/en/', 'ggg', 2),
(21, 'Sanjusangendo', 'Sanjusangendo (三十三間堂, Sanjūsangendō) is the popular name for Rengeo-in, a temple in eastern Kyoto which is famous for its 1001 statues of Kannon, the goddess of mercy. The temple was founded in 1164 and rebuilt a century later after the original structure had been destroyed in a fire.', 600, 'http://www.sanjusangendo.jp/', 'ggg', 2),
(22, 'Nanzenji Temple', 'Nanzenji Temple (南禅寺), whose spacious grounds are located at the base of Kyoto''s forested Higashiyama mountains, is one of the most important Zen temples in all of Japan. It is the head temple of one of the schools within the Rinzai sect of Japanese Zen Buddhism and includes multiple subtemples, that make the already large complex of temple buildings even larger.', 2200, 'http://www.nanzen.net/english/', 'ggg', 2),
(23, 'Osaka Castle', 'The construction of Osaka Castle (大阪城, Ōsakajō) started in 1583 on the former site of the Ishiyama Honganji Temple, which had been destroyed by Oda Nobunaga thirteen years earlier. Toyotomi Hideyoshi intended the castle to become the center of a new, unified Japan under Toyotomi rule. It was the largest castle at the time.', 800, 'http://www.osakacastle.net/english/', 'ggg', 3),
(24, 'Shitenno-ji Temple', 'Shitennoji (四天王寺, Shitennōji) is one of Japan''s oldest temples and the first ever to be built by the state. It was founded in 593 by Prince Shotoku, who supported the introduction of Buddhism into Japan. Although the temple''s buildings burned down several times throughout the centuries, they were always carefully reconstructed to reflect the original 6th century design.', 600, 'http://www.shitennoji.or.jp/', 'ggg', 3),
(25, 'Sumiyoshi Taisha', 'Osaka''s Sumiyoshi Taisha (住吉大社, "Sumiyoshi Grand Shrine") is one of Japan''s oldest shrines. Founded in the 3rd century before the introduction of Buddhism, it displays a unique style of shrine architecture, called Sumiyoshi-zukuri, that is free of influence from the Asian mainland. Only two other shrine architecture styles are also considered purely Japanese: Shinmei-zukuri as seen at the Ise Shrines and Taisha-zukuri as seen at Izumo Taisha.', 0, 'http://www.sumiyoshitaisha.net/en/', 'ggg', 3),
(26, 'Osaka Science Museum', 'text', 400, 'https://www.sci-museum.jp/info/guide/', 'ggg', 3),
